class NodeMultiface : virtual public NodeIf {
 public:
  NodeMultiface(std::vector<boost::shared_ptr<NodeIf> >& ifaces) : ifaces_(ifaces) {
  }
  virtual ~NodeMultiface() {}
 protected:
  std::vector<boost::shared_ptr<NodeIf> > ifaces_;
  NodeMultiface() {}
  void add(boost::shared_ptr<NodeIf> iface) {
    ifaces_.push_back(iface);
  }
 public:
  void find_successor(finger_entry& _return, const finger_entry& caller) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->find_successor(_return, caller);
        return;
      } else {
        ifaces_[i]->find_successor(_return, caller);
      }
    }
  }

  void closest_preceding_finger(finger_entry& _return, const int32_t id) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->closest_preceding_finger(_return, id);
        return;
      } else {
        ifaces_[i]->closest_preceding_finger(_return, id);
      }
    }
  }

  void get_successor(finger_entry& _return) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->get_successor(_return);
        return;
      } else {
        ifaces_[i]->get_successor(_return);
      }
    }
  }

  void get_predecessor(finger_entry& _return) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->get_predecessor(_return);
        return;
      } else {
        ifaces_[i]->get_predecessor(_return);
      }
    }
  }

  void notify(const finger_entry& n) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      ifaces_[i]->notify(n);
    }
  }

  int32_t add_file(const int32_t key_id, const std::string& s) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        return ifaces_[i]->add_file(key_id, s);
      } else {
        ifaces_[i]->add_file(key_id, s);
      }
    }
  }

  int32_t del_file(const int32_t key_id) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        return ifaces_[i]->del_file(key_id);
      } else {
        ifaces_[i]->del_file(key_id);
      }
    }
  }

  void get_file(file_data& _return, const int32_t key_id) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->get_file(_return, key_id);
        return;
      } else {
        ifaces_[i]->get_file(_return, key_id);
      }
    }
  }

  void get_table(node_table& _return, const int32_t id) {
    size_t sz = ifaces_.size();
    for (size_t i = 0; i < sz; ++i) {
      if (i == sz - 1) {
        ifaces_[i]->get_table(_return, id);
        return;
      } else {
        ifaces_[i]->get_table(_return, id);
      }
    }
  }

};

